	REG_TYPE  r1     = x0[0]  + x1[0];
	REG_TYPE  r2     = MULT_NORM(r1     * T[0]   + r0 * T[1]);
	REG_TYPE  r3     = MULT_NORM(r1     * T[1]   - r0 * T[0]);

