			  r0     = HALVE(x0[1] + x1[1]);
			  r1     = HALVE(x0[0] - x1[0]);

			  w0[0]  = r0     + r2;
			  w1[2]  = r0     - r2;
			  w0[1]  = r1     + r3;
			  w1[3]  = r3     - r1;

			  x0     = x+bit[2];
			  x1     = x+bit[3];

			  r0     = x0[1]  - x1[1];
			  r1     = x0[0]  + x1[0];
			  r2     = MULT_NORM(r1     * T[2]   + r0 * T[3]);
			  r3     = MULT_NORM(r1     * T[3]   - r0 * T[2]);

			  r0     = HALVE(x0[1] + x1[1]);
			  r1     = HALVE(x0[0] - x1[0]);

			  w0[2]  = r0     + r2;
			  w1[0]  = r0     - r2;
			  w0[3]  = r1     + r3;
			  w1[1]  = r3     - r1;

			  T     += 4;
			  bit   += 4;
			  w0    += 4;

