			b->timeRefLow = ByteOrder::swapIfBigEndian ((uint32) (time & 0xffffffff));
			b->timeRefHigh = ByteOrder::swapIfBigEndian ((uint32) (time >> 32));

			values [WavAudioFormat::bwavCodingHistory].copyToUTF8 (b->codingHistory, 0x7fffffff);

